package com.salesianostriana.dam.ejerapirestmonumentos.controller;import com.salesianostriana.dam.ejerapirestmonumentos.dto.EditMonumentoCommand;import com.salesianostriana.dam.ejerapirestmonumentos.model.Monumento;import com.salesianostriana.dam.ejerapirestmonumentos.services.MonumentoService;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/monumentos")@RequiredArgsConstructorpublic class MonumentoController {    private final MonumentoService monumentoService;    @GetMapping    public List<Monumento> getAll(){        return  monumentoService.finAll();    }    @GetMapping ("/{id}")    public ResponseEntity<Monumento> getById(@PathVariable Long id){        return  ResponseEntity.status(HttpStatus.CREATED).body(                monumentoService.findById(id)        );    }    @PostMapping    public ResponseEntity<Monumento> createMonumento(            @RequestBody EditMonumentoCommand cmd            ){        return  ResponseEntity.status(HttpStatus.CREATED)                .body(monumentoService.save(cmd));    }    @PutMapping("/{id}")    public Monumento edit (@RequestBody EditMonumentoCommand cmd, @PathVariable Long id){        return  monumentoService.edit(cmd,id);    }    @DeleteMapping("/{id}")    public ResponseEntity<?> delete (@PathVariable Long id){        monumentoService.delete(id);        return  ResponseEntity.noContent().build();    }}